var LaraspaceLayout = function () {

    var toggleSideBar = function (){
        var icon = $('.hamburger').first();
        $('body').toggleClass('sidebar-open');
        icon.toggleClass('is-active');
    };

    var handleSideBar = function () {
        $("#menu").metisMenu();

        // Mobile Toggle
        $('.nav-toggle').on('click',function(e){
            e.preventDefault();
            toggleSideBar();
        });

    };

    var handleMobileOverlay = function () {
        $('.mobile-menu-overlay').on('click',function(e){
            e.preventDefault();

            toggleSideBar();
        });
    };



    var handleCustomScrollBars = function(){


        // setTimeout(function(){
        //     $(".scroll-pane").mCustomScrollbar({theme:"minimal-dark"});
        // },1000);

        $(".scroll-pane").mCustomScrollbar({theme:"minimal-dark"});
    };


    return {
        //main function to initiate the module
        init: function () {
            handleSideBar();
            handleMobileOverlay();
            handleCustomScrollBars();
        }
    };

}();

jQuery(document).ready(function() {
    LaraspaceLayout.init();
});
var LaraspaceNotifs = function () {

    var handleNotifs = function(){
        toastr.options = {
            "closeButton": true,
            "debug": false,
            "positionClass": "toast-top-right",
            "onclick": null,
            "showDuration": "1000",
            "hideDuration": "1000",
            "timeOut": "5000",
            "extendedTimeOut": "1000",
            "showEasing": "swing",
            "hideEasing": "linear",
            "showMethod": "fadeIn",
            "hideMethod": "fadeOut"
        };

        var notify = $('.laraspace-notify'),
            type = notify.data('notify'),
            driver = notify.data('driver'),
            message = notify.data('message'),title = notify.data('title');

        //Laracasts-Flash returns danger as the error type. However we need it to "error" to work with toastr.
        if(type == 'danger'){
            type = 'error';
        }

        if(!message == '' && driver == 'toastr'){
            toastr[type](message, title);
        }else if(!message == ''){
            notieAlert(type,message);
        }

    };

    var notieAlert = function(type,message){

        //Change timeout value here.
        let timeout = 5;

        switch(type) {
            case 'success':
                notie.alert({
                    type : 1,
                    text : message,
                    time : timeout
                })
                break;
            case 'warning':
                notie.alert({
                    type : 2,
                    text : message,
                    time : timeout
                })
                break;
            case 'info':
                notie.alert({
                    type : 3,
                    text : message,
                    time : timeout
                })
                break;
            case 'error':
                notie.alert({
                    type : 4,
                    text : message,
                    time : timeout
                })
                break;
            default:
                notie.alert({
                    type : 1,
                    text : message,
                    time : timeout
                })

        }
    };

    return {
        //main function to initiate the module
        init: function () {
            handleNotifs();
        },
    };

}();

jQuery(document).ready(function() {
    LaraspaceNotifs.init();
});
var LaraspaceForms = function () {

    var handleSelect2 = function(){
        $(".ls-select2").select2();
    };

    var handleBootstrapSelect = function(){
        $(".ls-bootstrap-select").selectpicker({
            iconBase: 'fa',
            tickIcon: 'fa-check',
            container: 'body'
        });
    };

    var handleSwitchToggles = function(){
        var elems = $('.ls-switch');

        elems.each(function(index,element) {
            var color = $(this).data('color') ? $(this).data('color') : '#ffde00';
            var size = $(this).data('size') ? $(this).data('size') : 'default';

            var switchery = new Switchery(this, {
                color : color,
                size : size
            });
        });
    };

    var handleTimePickers = function(){
        $('.ls-clockpicker').clockpicker({
            donetext: 'Done'
        });


        var elems = $('.ls-timepicker');

        elems.each(function(index,element) {
            var timeFormat = $(this).data('format') ? $(this).data('format') : 'g:ia';
            var showDuration = $(this).data('duration') ? $(this).data('duration') : false;

            $(this).timepicker({
                timeFormat : timeFormat,
                showDuration : showDuration,
                minTime: '2:00pm',
                maxTime: '11:30pm'
            });
        });


    };

    var handleMultiSelect = function(){
        $('.ls-multi-select').multiSelect()
    };

    var handleDatePicker = function(){
        $('.ls-datepicker').datepicker();
    };

    var handleEditors = function(){
        $('.ls-summernote').summernote();

        var editor = $('.ls-simplemde')[0];

        if(editor){
            var simplemde = new SimpleMDE({ element: editor });
        }

    };

    var handleEasyPieCharts = function(){

        var elems = $('.easy-pie-chart');

        elems.each(function(index,element) {
            var color = $(this).data('color') ? $(this).data('color') : '#ffde00';

            $(this).easyPieChart({
                scaleColor : false,
                barColor : color,
                trackColor : '#f8f8f8',
                size : 80,
                onStep: function(from, to, percent) {
                    $(this.el).find('.percent').text(Math.round(percent));
                }
            });
        });

    };



    return {
        //main function to initiate the module
        init: function () {
            handleSelect2();
            handleBootstrapSelect();
            handleSwitchToggles();
            handleTimePickers();
            handleDatePicker();
            handleMultiSelect();
            handleEditors();
            handleEasyPieCharts();
        }
    };

}();

jQuery(document).ready(function() {
    LaraspaceForms.init();
});

